css:
body {
    font-family: "Arabic Typesetting", "Traditional Arabic", Arial, sans-serif;
    color: #000;
    margin: 20px;
}
.header {
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    direction: rtl;
}

.header img {
    height: 100px;
    display: block;
}
.header .center-text {
    flex: 1;
    text-align: center;
    line-height: 1.7;
}
.header .center-text .title-large {
    font-size: 28px;
    font-weight: bold;
}
.header .center-text .title-medium {
    font-size: 24px;
}
hr {
    border: 1px solid #000;
    margin: 10px 0 20px 0;
}
.info-section {
    display: flex;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 20px;
}
.info-section div {
    width: 45%;
    text-align: right;
}

.info-section p {
    margin: 3px 0;
}

table.receipt-table {
    width: 100%;
    direction: rtl;
    border-collapse: collapse;
    font-size: 12px;
    color: #000;
}

table.receipt-table th {
    background-color: #d9d9d9;
    font-weight: bold;
    text-align: right;
    border: 1px solid #000;
    padding: 8px;
}

table.receipt-table td {
    border: 1px solid #000;
    padding: 5px;
    text-align: right;
}

table.receipt-table tbody tr:nth-child(even) {
    background-color: #f9f9f9;
}

table.receipt-table tbody tr:nth-child(odd) {
    background-color: #ffffff;
}

table.receipt-table tbody tr:hover {
    background-color: #e6f7ff;
}
.info-section {
    display: flex;
     justify-content: space-between;
     direction: rtl;
     font-family: Arial, sans-serif;
      width:100%;
}
@media print {
    body {
        margin: 0;
    }

    .header img {
        height: 80px;
    }

    table.receipt-table {
        font-size: 12px;
    }
}
